function dropdown(a, b='') {
 var drop = 
`<div class="dropdown mx-1 ${b}">
     <button type="button" class="dropdown-toggle btn btn-primary p-1 w-100 small" data-bs-toggle="dropdown" data-bs-auto-close="false">
     ${a}
     </button>
     <ul class="dropdown-menu">
     <li class="${a} dropdown-item text-wrap w-100">
     </li>
     </ul>
</div>`;
		return drop;
	}

async function ajax(u, back, cb) {
    const cached = sessionStorage.getItem(u);
    if (cached) {
      const { timestamp, data } = JSON.parse(cached);
      const now = new Date().getTime();
      const fiveMinutes = 1 * 60 * 60 * 1000;
      if (now - timestamp < fiveMinutes) {
        cb(data);
        return;
      } else {
          console.log('expired')
      }
    }

    await $.ajax({
        url:u,
       beforeSend: function(xhr) {
  xhr.setRequestHeader('Access-Control-Allow-Origin', '*');

        },
        type:'GET',
        crossDomain: true,
        jsonpCallback: back,
        dataType:'jsonp',
        success:function(res) {
          sessionStorage.setItem(u,JSON.stringify(res));
          cb(res);
         sessionStorage.setItem(u, JSON.stringify({
            timestamp: new Date().getTime(),
            data: res
          }));
      },
        error: function(xhr, status, error) {
            console.error("AJAX request failed:", status, error);
        }
    });

}



